if($this->target_format != 'PDF'){
                $checks = $_criteria["Hidecolumns"]->get_criteria_value("VALUE",false);

                for ($ct = 0; $ct < count($this->columns); $ct++)
                {
                $col = $this->columns[$ct];
                if ( $col->query_name == "ref" && preg_match("/[1]/", $checks) )
                {
                $col->attributes["column_display"] = "hide";
                }
                if ( $col->query_name == "customer" && preg_match("/[2]/", $checks) )
                {
                $col->attributes["column_display"] = "hide";
                }
                if ( $col->query_name == "fecha" && preg_match("/[3]/", $checks) )
                {
                $col->attributes["column_display"] = "hide";
                }
                if ( $col->query_name == "status" && preg_match("/[4]/", $checks) )
                {
                $col->attributes["column_display"] = "hide";
                }
                if ( $col->query_name == "base" && preg_match("/[5]/", $checks) )
                {
                $col->attributes["column_display"] = "hide";
                }
                if ( $col->query_name == "vat" && preg_match("/[6]/", $checks) )
                {
                $col->attributes["column_display"] = "hide";
                }
